// HTML'i parse et ve ürün bilgilerini çıkar
const html = $input.all()[0].json.body;
const deals = [];

// Basit regex ile başlık ve link çıkarma (gerçek kullanımda cheerio veya benzeri kütüphane gerekebilir)
const titleRegex = /<a[^>]*class="[^"]*topic[^"]*"[^>]*>([^<]+)<\/a>/gi;
const linkRegex = /<a[^>]*href="([^"]+)"[^>]*class="[^"]*topic[^"]*"/gi;

let match;
const titles = [];
const links = [];

while ((match = titleRegex.exec(html)) !== null) {
  titles.push(match[1].trim());
}

while ((match = linkRegex.exec(html)) !== null) {
  const url = match[1];
  const fullUrl = url.startsWith('http') ? url : `https://forum.donanimarsivi.com${url}`;
  links.push(fullUrl);
}

// Başlık ve linkleri eşleştir
for (let i = 0; i < Math.min(titles.length, links.length); i++) {
  const title = titles[i];
  const url = links[i];

  // İndirim bitti kontrolü
  const isExpired = title.toLowerCase().includes('indirim bitti') || 
                    title.toLowerCase().includes('x indirim');

  // Fiyatı title'dan çıkar
  const priceMatch = title.match(/(\d+[.,]\d+)\s*TL/);
  const price = priceMatch ? priceMatch[1] + ' TL' : null;

  if (!isExpired && title && url) {
    deals.push({
      source: 'DonanımArsivi',
      title: title,
      price: price,
      url: url
    });
  }
}

return deals.map(deal => ({ json: deal }));
